export default {
	addNewSkill: () => {
		if((Input1.text) && (catSkillSelect.selectedOptionValue) && (typeSkillSelect.selectedOptionValue)){ //เช็คว่าทุกช่องต้องมีข้อมูล

			insertSkill.run(
				() => showAlert('เพิ่มสำเร็จ'), // กรณีสำเร็จ
				() => showAlert('เพิ่มไม่สำเร็จ','error') //มีรูปแบบของการแสดงผล showAlert() 4 แบบ คือ info, success, error, warning
			)
		}else{
			showAlert('โปรดระบุข้อมูลให้ครบถ้วน');
		}
	},
	addNewSkill2: () => {
		if((Input1.text) && (catSkillSelect.selectedOptionValue) && (typeSkillSelect.selectedOptionValue)){ //เช็คว่าทุกช่องต้องมีข้อมูล

			insertSkill.run(
				() => showAlert('เพิ่มสำเร็จ'), // กรณีสำเร็จ
				() => showAlert(insertSkill.data.message, 'error') //มีรูปแบบของการแสดงผล showAlert() 4 แบบ คือ info, success, error, warning ใส่เพิ่มหรือไม่เพิ่มก็ได้
			)
		}else{
			showAlert('โปรดระบุข้อมูลให้ครบถ้วน');
		}
	}
}
